import React, { useState } from 'react';
import {
 StyleSheet,
 View,
 TouchableOpacity,
 Text,
 PanResponder,
 Animated,
} from 'react-native';

const App = () => {
 const [buttonPressed, setButtonPressed] = useState(null);

 const handlePress = (index) => {
    setButtonPressed(index);
 };

 const handleRelease = () => {
    setButtonPressed(null);
 };

 const joyStickValue = new Animated.ValueXY();

 const joyStickResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onPanResponderGrant: () => {
      joyStickValue.setOffset({ x: joyStickValue.x._value, y: joyStickValue.y._value });
      joyStickValue.setValue({ x: 0, y: 0 });
    },
    onPanResponderMove: Animated.event([null, { dx: joyStickValue.x, dy: joyStickValue.y }]),
    onPanResponderRelease: () => {
      joyStickValue.flattenOffset();
    },
 });

 return (
    <View style={styles.container}>
      <Animated.View style={[styles.joyStick, joyStickValue.getLayout()]} {...joyStickResponder.panHandlers} />
      <View style={styles.buttonContainer}>
        <TouchableOpacity onPressIn={() => handlePress(1)} onPressOut={handleRelease}>
          <Text style={buttonPressed === 1 ? styles.buttonPressed : styles.button}>Button 1</Text>
        </TouchableOpacity>
        <TouchableOpacity onPressIn={() => handlePress(2)} onPressOut={handleRelease}>
          <Text style={buttonPressed === 2 ? styles.buttonPressed : styles.button}>Button 2</Text>
        </TouchableOpacity>
        <TouchableOpacity onPressIn={() => handlePress(3)} onPressOut={handleRelease}>
          <Text style={buttonPressed === 3 ? styles.buttonPressed : styles.button}>Button 3</Text>
        </TouchableOpacity>
      </View>
    </View>
 );
};

const styles = StyleSheet.create({
 container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
 },
 joyStick: {
    width: 100,
    height: 100,
    borderRadius: 50,
    backgroundColor: 'blue',
    alignItems: 'center',
    justifyContent: 'center',
 },
 buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    width: '100%',
    paddingHorizontal: 50,
 },
 button: {
    backgroundColor: 'grey',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 5,
 },
 buttonPressed: {
    backgroundColor: 'green',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 5,
 },
});

export default App;
