import React, { useState } from 'react';
import { StyleSheet, View, TouchableOpacity, Text } from 'react-native';

const App = () => {
 const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });
 const [isCircleVisible, setIsCircleVisible] = useState(false);

 const handlePress = (event) => {
    const { locationX, locationY } = event.nativeEvent;
    setCirclePosition({ x: locationX, y: locationY });
    setIsCircleVisible(true);
 };

 const renderCircle = () => {
    if (!isCircleVisible) return null;

    const size = 50;
    const style = [
      styles.circle,
      {
        left: circlePosition.x - size / 2,
        top: circlePosition.y - size / 2,
      },
    ];

    return <View style={style} />;
 };

 return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.button} onPress={handlePress}>
        <Text style={styles.buttonText}>Draw a circle</Text>
      </TouchableOpacity>
      {renderCircle()}
    </View>
 );
};

const styles = StyleSheet.create({
 container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
 },
 button: {
    alignItems: 'center',
    backgroundColor: '#DDDDDD',
    padding: 10,
    marginBottom: 10,
 },
 buttonText: {
    fontSize: 16,
 },
 circle: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: 'blue',
    position: 'absolute',
 },
});

export default App;
