import React, { useState, useEffect } from 'react';
import { Button, View, Text, StyleSheet } from 'react-native';

const MyApp = () => {
 const [playerList, setPlayerList] = useState([]);
 const [number, setNumber] = useState(null);
 const [isPressed, setIsPressed] = useState(false);

 useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://192.168.0.129:1337/api/player-list');
        const data = await response.json();
        setPlayerList(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
 }, []);

 const handleButtonPress = async () => {
    setIsPressed(true);

    const data = {
      press_n: number,
      is_pressed: isPressed,
    };

    try {
      const response = await fetch('http://192.168.0.129:1337/api/player', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (response.text !== 'success') {
        throw new Error('Failed to send data');
      }
    } catch (error) {
      console.error('Error:', error);
    }

    if (number === 3) {
      quitApp();
    }
 };

 const quitApp = () => {
    return (
      <View style={styles.container}>
        <Text>Ага щас. Отсюда еще никто не уходил</Text>
      </View>
    );
 };

 if (!Object.keys(playerList).length) {
    return (
      <View style={styles.container}>
        <Text>Нет игроков - </Text>
		<Text>{JSON.stringify(playerList)}</Text>
      </View>
    );
 }

 return (
    <View style={styles.container}>
      <Button title="Press me" onPress={handleButtonPress} />
    </View>
 );
};

const styles = StyleSheet.create({
 container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
 },
});

export default MyApp;
