import React, { useState, useEffect, useRef } from 'react';
import { StyleSheet, View, Button, PanResponder, Dimensions } from 'react-native';

const App = () => {
  const [joystickPos, setJoystickPos] = useState({ x: 0, y: 0 });
  const screenWidth = Dimensions.get('window').width;
  const screenHeight = Dimensions.get('window').height;
  const center = { x: screenWidth / 2, y: screenHeight / 2 };
  const radius = 100;

  const panResponder = useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderMove: (evt, gestureState) => {
        updateJoystick(gestureState.moveX, gestureState.moveY);
      },
      onPanResponderRelease: () => {
        setJoystickPos({ x: center.x, y: center.y });
      },
    })
  ).current;

  const touchCircle = (x, y) => {
    return Math.sqrt((x - center.x) ** 2 + (y - center.y) ** 2) <= radius;
  };

  const clamp = (value, min, max) => {
    return Math.min(Math.max(value, min), max);
  };

  const touchCirclePosition = (x, y) => {
    return {
      x: clamp(x, center.x - radius, center.x + radius),
      y: clamp(y, center.y - radius, center.y + radius),
    };
  };

  const updateJoystick = (x, y) => {
    if (touchCircle(x, y)) {
      setJoystickPos(touchCirclePosition(x, y));
    } else {
      setJoystickPos({ x: center.x, y: center.y });
    }
  };

  return (
    <View style={styles.container}>
      <View
        {...panResponder.panHandlers}
        style={styles.touchArea}
      />
      <View style={[styles.joystick, { left: joystickPos.x, top: joystickPos.y }]} />
      <View style={styles.buttonsContainer}>
        <Button title="Button 1" onPress={() => {}} />
        <Button title="Button 2" onPress={() => {}} />
        <Button title="Button 3" onPress={() => {}} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  touchArea: {
    width: 200,
    height: 200,
    borderRadius: 100,
    borderWidth: 2,
    borderColor: 'black',
  },
  joystick: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: 'blue',
    position: 'absolute',
  },
  buttonsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    width: '100%',
    marginTop: 50,
  },
});

export default App;
